    const[search,setSearch]=useState("")
    function handleChange(e){
        setSearch(e.target.value)
        onSearching(search)

    }
    return (
            <form id="search-form">
                <input onChange={handleChange} id="search" placeholder="Search Transanctions..." type={"text"}/>
                <button><FontAwesomeIcon icon={faSearch}/></button>
            </form>
    )
}
